import React, { useContext } from 'react'
import TodoContext from '../context/TodoContext'

const TodoList = () => {
    const [tasks] = useContext(TodoContext)
    const sortedTask = tasks.sort((a, b) => a.time - b.time)

    return (
        <>
            <div className="">
                <ul className="divide-y-2">
                    {tasks.map(task => {
                        return (
                            <li key={sortedTask.id} className='p-6 flex gap-4 items-center'>
                                <input type="checkbox" name="" id=""/>
                                <p className='grow w-2/4 text-sm font-normal text-slate-600 line-clamp-2'>{sortedTask.title}</p>
                                <p className='text-sm font-normal text-slate-600'>{sortedTask.time}</p>
                            </li>
                        )
                    })}
                </ul>
            </div>
        </>
    )
}

export default TodoList
