import React, { useEffect, useState } from 'react'

const Home = () => {
    const [date, setDate] = useState('')
    const [day, setDay] = useState('')
    const [month, setMonth] = useState('')
    const weekday = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
    useEffect(()=>{
        const newDate = new Date(Date.now())
        setDate(newDate.getDate())
        setDay(newDate.getDay())
        setMonth(newDate.getMonth())
    },[])
    return (
        <>
            <div className="container max-w-md mx-auto mt-10 rounded-lg shadow-md bg-slate-100 text-purple-600 p-6 flex justify-between items-center">
                <div className='text-lg'>
                    <p className='font-semibold'>{weekday[day]}, <span className='font-normal'>{date}</span></p>
                    <p className='text-sm font-normal text-slate-400'>{month}</p>
                </div>
                <div>
                    <p className='text-sm font-normal text-slate-400'>10 Tasks</p>
                </div>
            </div>
        </>
    )
}

export default Home
