import React, { useContext, useEffect, useState } from 'react'
import TodoContext from '../context/TodoContext'

const Home = () => {
    const [date, setDate] = useState('')
    const [day, setDay] = useState('')
    const [month, setMonth] = useState('')
    const [tasks, setTasks] = useState([])
    const weekday = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

    const getTodos = () => {
        let data = useContext(TodoContext)
        setTasks(data)
    }
    useEffect(() => {
        const newDate = new Date()
        setDate(newDate.getDate())
        setDay(newDate.getDay())
        setMonth(newDate.getMonth())
        getTodos()
    }, [])
    return (
        <>
        <TodoContext.Provider value={{tasks, setTasks}}>
                <div className="container max-w-md mx-auto mt-10 rounded-lg shadow-md bg-slate-100 text-purple-600 p-6 flex justify-between items-center">
                    <div className='text-lg'>
                        <p className='font-semibold'>{weekday[day]}, <span className='font-normal'>{date}</span></p>
                        <p className='text-sm font-normal text-slate-400'>{months[month]}</p>
                    </div>
                    <div>
                        <p className='text-sm font-normal text-slate-400'>{tasks.length} Tasks</p>
                    </div>
                </div>
        </TodoContext.Provider>
        </>
    )
}

export default Home
