import React, { useContext, useState } from 'react'
import DateSection from './DateSection'
import { useNavigate } from "react-router-dom";
import TodoContext from '../context/TodoContext';

const AddTodos = () => {
    const [newTitle, setNewTitle] = useState('');
    const [newTime, setNewTime] = useState('');
    const navigate = useNavigate()
    const [tasks, setTasks] = useContext(TodoContext)
    let validate = ''
    const saveTodo = (e) => {
        e.preventDefault()
        console.log(newTitle)
        console.log(newTime)
        const allTasks = [...tasks]
        const lastId = allTasks.length+1
        if(newTime !== '' || newTime.length === 0 ){

        }
        allTasks.push({
            id: lastId,
            title: newTitle,
            time: newTime
        })
        setTasks(allTasks)
        setNewTitle('')
        setNewTime('')
        navigate('/')
    }

    

    return (
        <>
            <DateSection showAdd={false} />
            <div>
                <form onSubmit={saveTodo} className='p-6 flex flex-col gap-4 text-sm font-normal text-slate-600'>
                    <label htmlFor="title">Enter Task</label>
                    <input type="text" className={validate+ 'p-1'} name="title" id="title" value={newTitle} onChange={e=>setNewTitle(e.target.value)} placeholder="Enter Task"/>
                    <label htmlFor="time">Select Time</label>
                    <input type="time" name="time" id="time" value={newTime} onChange={e=>setNewTime(e.target.value)} placeholder="Select Time"/>
                    <button className="bg-purple-600 text-slate-100 px-6 py-3 font-semibold text-normal" type="submit">
                        Save
                    </button>
                </form>
            </div>
        </>
    )
}

export default AddTodos
